import React, { useState } from "react";
import "../styles/Advice.css";
import ActionButton from "../components/Actionbutton";
import QuestionCard from "../components/Questioncard";
import PostCard from "../components/Postcard";
import SearchBar from "../components/Searchbar";

function Advice() {
  const [isPostCardOpenForQuestion, setIsPostCardOpenForQuestion] = useState(false);
  const [isPostCardOpenForAdvice, setIsPostCardOpenForAdvice] = useState(false);

  const handleOpenPostCardForQuestion = () => {
    setIsPostCardOpenForQuestion(true);
  };

  const handleOpenPostCardForAdvice = () => {
    setIsPostCardOpenForAdvice(true);
  };

  const handleClosePostCard = () => {
    setIsPostCardOpenForQuestion(false);
    setIsPostCardOpenForAdvice(false);
  };

  return (
    <div>
      <SearchBar />
      <div className="AdviceButtonContainer">
        {!isPostCardOpenForQuestion && <ActionButton skill="Ask Question" onClick={handleOpenPostCardForQuestion} />}
        {!isPostCardOpenForAdvice && <ActionButton skill="Add Advice" onClick={handleOpenPostCardForAdvice} />}
      </div>
      <div className="AdviceInputContainer">
        {(isPostCardOpenForQuestion || isPostCardOpenForAdvice) && (
          <PostCard onClose={handleClosePostCard} />
        )}
      </div>
      <div className="AdvicePostsContainer">
        <QuestionCard cardID="1" />
        <QuestionCard cardID="2" />
      </div>
    </div>
  );
}

export default Advice;
