import * as React from "react";
import Card from "@mui/joy/Card";
import CardContent from "@mui/joy/CardContent";
import Link from "@mui/joy/Link";
import Input from "@mui/joy/Input";
import Typography from "@mui/joy/Typography";

const initialState = {
  comment: "",
  commentsList: [],
};

const reducer = (state, action) => {
  switch (action.type) {
    case "setComment":
      return { ...state, comment: action.payload };
    case "addComment":
      return {
        ...state,
        commentsList: [...state.commentsList, action.payload],
        comment: "",
      };
    default:
      return state;
  }
};

export default function Questioncard({ cardId }) {
  const [state, dispatch] = React.useReducer(reducer, initialState);

  React.useEffect(() => {
    const storedComments = localStorage.getItem(`comments_${cardId}`);
    if (storedComments) {
      dispatch({ type: "setComment", payload: storedComments });
    }
  }, [cardId]);

  React.useEffect(() => {
    localStorage.setItem(`comments_${cardId}`, JSON.stringify(state.commentsList));
  }, [state.commentsList, cardId]);

  const handlePostClick = () => {
    if (state.comment.trim() === "") {
      return;
    }

    dispatch({ type: "addComment", payload: { id: Date.now(), text: state.comment } });
  };

  return (
    <Card
      variant="outlined"
      sx={{
        minWidth: 300,
        width: "25%",
        "--Card-radius": (theme) => theme.vars.radius.xs,
      }}
    >
      <CardContent
        orientation="horizontal"
        sx={{ alignItems: "center", gap: 1 }}
      ></CardContent>

      <CardContent>
        <Link
          component="button"
          underline="none"
          fontSize="sm"
          fontWeight="lg"
          textColor="text.primary"
        ></Link>
        <Typography fontSize="sm">
          <Link
            component="button"
            color="neutral"
            fontWeight="lg"
            textColor="text.primary"
          >
            ***
          </Link>{" "}
          ***
        </Typography>
        <Link
          component="button"
          underline="none"
          fontSize="sm"
          startDecorator="…"
          sx={{ color: "text.tertiary" }}
        >
          more
        </Link>
        <Link
          component="button"
          underline="none"
          fontSize="10px"
          sx={{ color: "text.tertiary", my: 0.5 }}
        ></Link>
      </CardContent>
      {state.commentsList.map((commentItem) => (
        <CardContent key={commentItem.id} sx={{ mt: 1 }}>
          {commentItem.text}
        </CardContent>
      ))}
      <CardContent orientation="horizontal" sx={{ gap: 1 }}>
        <Input
          variant="plain"
          size="sm"
          placeholder="Add a comment…"
          value={StaticRange.comment}
          onChange={(e) => setComment(e.target.value)}
          sx={{ flex: 1, px: 0, "--Input-focusedThickness": "0px" }}
        />
        <Link onClick={handlePostClick} underline="none" role="button">
          Post
        </Link>
      </CardContent>
    </Card>
  );
}
