import * as React from "react";
import Button from "@mui/material/Button";
import { Typography } from "@mui/material";
import PostCard from "./Postcard"; 


export default function ActionButton({ skill, openPost }) {
  const [isPostCardOpen, setIsPostCardOpen] = React.useState(false);

  const handleClick = () => {
    setIsPostCardOpen(!isPostCardOpen);
  };

  return (
    <div
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
        gridGap: "10px",
        padding: "10px",
        justifyItems: "center",
      }}
    >
        <Button
          variant="contained"
          size="medium"
          sx={{ bgcolor: "#ffff", color: "#233349", borderRadius: "10px" }}
          onClick={handleClick}
        >
          <Typography
            sx={{
              fontFamily: "Arial",
              textTransform: "initial",
              whiteSpace: "nowrap",
            }}
          >
            {skill}
          </Typography>
        </Button>
      {isPostCardOpen && openPost && <PostCard />} 
    </div>
  );
}
