import React from 'react';
import ClickSound from "./clicksound.wav";

const CustomButton = () => {
    let audio = new Audio(ClickSound);

    const start = () => {
        audio.play()
    };
    
    const buttonStyle = {
        border: "solid",
        borderRadius: "15px",
    }

    return (
        <div style={buttonStyle}>
            <button onClick={start}> Play Sound</button>
        </div>
    );
};

export default CustomButton;
